aha.on("fred", ({ record }, { identifier, settings }) => {
  FredController.instance.show()

  setTimeout(() => {
    FredController.instance.hide()
  }, 8000)
});

const image = ''


class FredController {
  constructor() {
    this.el = null
  }

  isMounted() {
    return !!document.querySelector('#fred')
  }

  mount() {
    if (this.isMounted()) return

    const container = document.querySelector('.top-nav__secondary')

    const newFred = document.createElement('img')
    newFred.id = 'fred'
    newFred.src = `data:image/png;base64,${image}`
    newFred.style.objectFit = 'cover'
    newFred.style.objectPosition = '0 2px'
    newFred.style.height = '100%'
    newFred.style.width = '80px'
    newFred.style.cursor = 'pointer'

    newFred.style.transition = 'transform 3s linear'
    newFred.style.transform = 'translateY(100%)'
    
    const wrapper = document.createElement('div')
    wrapper.style.overflow = 'hidden'
    wrapper.appendChild(newFred)
    container.prepend(wrapper)

    this.el = newFred
  }

  show() {
    if (!this.el) return
    this.el.style.transform = 'translateY(0)'
  }

  hide() {
    if (!this.el) return
    this.el.style.transform = 'translateY(100%)'
  }

}

FredController.instance = new FredController()

class BroadcastObserver {
  constructor() {
    this.observer = null
  }

  containsBroadcast(nodes) {
    return Array.from(nodes)
      .filter(e => e.id.startsWith('broadcast-'))
      .length > 0
  }

  observe() {
    if (this.observer) return

    // Register a mutation observer to watch for new broadcasts
    this.observer = new MutationObserver((mutations) => {
      let broadcastAdded, broadcastRemoved

      mutations.forEach(mutation => {
        if (mutation.type === 'childList') {
          broadcastAdded = mutation.addedNodes.length > 0 && this.containsBroadcast(mutation.addedNodes)
          broadcastRemoved = mutation.removedNodes.length > 0 && this.containsBroadcast(mutation.removedNodes)
        }
      })

      if (broadcastAdded) {
        FredController.instance.show()
      }
      if (broadcastRemoved) {
        FredController.instance.hide()
      }
    })
    this.observer.observe(document.body, { childList: true, subtree: true })

    // Also check for broadcasts already on the page
    if (this.containsBroadcast(document.querySelector('.broadcasts'))) {
      FredController.instance.show()
    }
  }
}
BroadcastObserver.instance = new BroadcastObserver()

const activateFred = () => {
  FredController.instance.mount()
  BroadcastObserver.instance.observe()
}

activateFred()
document.addEventListener("page:load", activateFred)